properties.widgets.S3-batchsource:
{
  "outputs": [{
    "name": "schema",
    "widget-type": "schema",
    "widget-attributes": {"default-schema": {
      "name": "fileRecord",
      "type": "record",
      "fields": [
        {
          "name": "offset",
          "type": "long"
        },
        {
          "name": "body",
          "type": "string"
        }
      ]
    }}
  }],
  "jump-config": {"datasets": [{"ref-property-name": "referenceName"}]},
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Basic",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "referenceName",
          "label": "Reference Name",
          "widget-attributes": {"placeholder": "Name used to identify this source for lineage"}
        },
        {
          "widget-type": "connection-browser",
          "widget-category": "plugin",
          "widget-attributes": {
            "label": "Browse",
            "connectionType": "S3"
          }
        },
        {
          "widget-type": "textbox",
          "name": "path",
          "label": "Path",
          "widget-attributes": {"placeholder": "s3a://<bucket>/path/to/input"}
        },
        {
          "widget-type": "select",
          "name": "format",
          "label": "Format",
          "widget-attributes": {
            "default": "text",
            "values": [
              "avro",
              "blob",
              "csv",
              "delimited",
              "json",
              "parquet",
              "text",
              "tsv"
            ]
          },
          "plugin-function": {
            "widget": "outputSchema",
            "missing-required-fields-message": "Please provide path field",
            "method": "POST",
            "required-fields": ["path"],
            "label": "Get Schema Value",
            "plugin-method": "getSchema"
          }
        },
        {
          "widget-type": "textbox",
          "name": "delimiter",
          "label": "Delimiter",
          "widget-attributes": {"placeholder": "Delimiter if the format is 'delimited'"}
        }
      ]
    },
    {
      "label": "Credentials",
      "properties": [
        {
          "widget-type": "radio-group",
          "name": "authenticationMethod",
          "label": "Authentication Method",
          "widget-attributes": {
            "layout": "inline",
            "default": "Access Credentials",
            "options": [
              {
                "id": "Access Credentials",
                "label": "Access Credentials"
              },
              {
                "id": "IAM",
                "label": "IAM"
              }
            ]
          }
        },
        {
          "widget-type": "textbox",
          "name": "accessID",
          "label": "Access ID",
          "widget-attributes": {"placeholder": "Amazon Access ID"}
        },
        {
          "widget-type": "password",
          "name": "accessKey",
          "label": "Access Key",
          "widget-attributes": {"placeholder": "Amazon Access Key"}
        }
      ]
    },
    {
      "label": "Advanced",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "maxSplitSize",
          "label": "Maximum Split Size",
          "widget-attributes": {"placeholder": "Maximum split size for each partition specified in bytes"}
        },
        {
          "widget-type": "textbox",
          "name": "fileRegex",
          "label": "Regex Path Filter",
          "widget-attributes": {"placeholder": "Regular expression for files to read"}
        },
        {
          "widget-type": "textbox",
          "name": "pathField",
          "label": "Path Field",
          "widget-attributes": {"placeholder": "Output field to contain the path of the object that was read from"}
        },
        {
          "widget-type": "radio-group",
          "name": "filenameOnly",
          "label": "Path Filename Only",
          "widget-attributes": {
            "layout": "inline",
            "default": "false",
            "options": [
              {
                "id": "true",
                "label": "True"
              },
              {
                "id": "false",
                "label": "False"
              }
            ]
          }
        },
        {
          "widget-type": "radio-group",
          "name": "recursive",
          "label": "Read Files Recursively",
          "widget-attributes": {
            "layout": "inline",
            "default": "false",
            "options": [
              {
                "id": "true",
                "label": "True"
              },
              {
                "id": "false",
                "label": "False"
              }
            ]
